import styled from "styled-components";

export const Container = styled.div`
  display: grid;
  justify-content: center;
  padding: 1.5rem;
  grid-template-columns: repeat(3, 1fr);
  grid-auto-flow: column;
  gap: 0;
`;

export const GridItem = styled.div`
  display: flex;
  justify-content: center;
`;
