import styled from "styled-components";

export const Container = styled.div`
  img {
    width: 100%;
    border-radius: 12px 12px 0 0;
  }
`;

export const Title = styled.p`
  font-weight: 500;
  font-size: 1.05rem;
  padding: 0.75rem 0 0.25rem 0;
  color: #444;
`;
