import styled from "styled-components";

export const Container = styled.div`
  width: 240px;
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
  background-color: #fff;
  padding-bottom: 1rem;
  border-radius: 12px;

  img {
    width: 100%;
    border-radius: 12px 12px 0 0;
  }
`;

export const Title = styled.p`
  font-weight: 400;
  font-size: 1.15rem;
  padding: 0.75rem 0 0.25rem 0;
  color: #00;
`;

export const Score = styled.p`
  margin-bottom: 0.65rem;
  width: 100%;
  text-align: center;
  font-size: 0.8rem;
  font-weight: 500;
  color: #ffbb3a;
`;

export const Ratings = styled.p`
  margin-top: 0.5rem;
  font-size: 0.7rem;
  color: #aaa;
`;
export const Button = styled.div`
  margin-top: 0.75rem;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;

  button {
    background-color: #4d41c0;
    cursor: pointer;
    width: 80%;
    padding: 0.65rem 0;
    outline: none;
    border: 0;
    border-radius: 4px;
    color: #fff;
    transition: all ease 0.1s;

    &:hover {
      background-color: #3c30b0;
    }
  }
`;
